"use strict";$("#login").validate({rules:{username:{required:!0,minlength:5,maxlength:10},password:{required:!0,minlength:6,maxlength:12}},messages:{},submitHandler:function(e){e=$(e).serialize();$.post("../server/login.php",e,null,"json").then(function(e){0===e.code?$(".login_error").removeClass("hide"):1===e.code&&($(".login_error").addClass("hide"),setCookie("nickname",e.nickname),window.location.href="../pages/index.html")})}});